fn decrypt_and_check(key: &String) {

    let img = String::from("");

    let decrypted = decrypt(&key, &img);

    // Use to write decrypted image out and verify it's correct
    //let fout = String::from("/home/thor/work/repos/fbctf-2019/reversing/imageprot/src/protect_the_graph_decrypted.jpg");
    //decrypt_file_and_write(&key, &img, &fout);

    let img_digest = md5::compute(&decrypted);
    assert_eq!(format!("{:x}", img_digest), "3f3c61ed02095a7970240054701a46d1");
}

// Helper function to encrypt data. Takes in a vector of bytes and returns a b64 string
fn encrypt(key: &String, clear: &Vec<u8>) -> String {
    let mut out_bytes : Vec<u8> = Vec::new();
    let key_bytes : Vec<u8> = key.as_bytes().to_vec();
    for i in 0..clear.len() {
        out_bytes.push(clear[i] ^ key_bytes[i % key_bytes.len()]);
    }
    //let encrypted_bytes = out_bytes.iter().map(|&c| c as char).collect::<String>();
    assert_eq!(out_bytes.len(), clear.len());
    base64::encode(&out_bytes)
}

// Helper function to decrypt data. Takes in a b64 string and returns a vec of bytes
fn decrypt(key: &String, enc: &String) -> Vec<u8> {
    let decoded : Vec<u8> = base64::decode(enc).unwrap();
    let mut out_bytes : Vec<u8> = Vec::new();
    let key_bytes : Vec<u8> = key.as_bytes().to_vec();
    for i in 0..decoded.len() {
        out_bytes.push(decoded[i] ^ key_bytes[i % key_bytes.len()]);
    }
    out_bytes
}

