// Fetch content from a specified URI
fn get_uri(uri: &String) -> String {
    let mut resp = reqwest::get(uri).expect("Failed to fetch URI");
    let mut body = String::new();
    resp.read_to_string(&mut body).expect("Failed to parse web content");
    body
}

